sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
   var ga = new GlideAjax('AiDevices');
   ga.addParam('sysparm_name', 'getAiDetails');
   ga.addParam('sysparm_device', g_form.getValue('u_ai_device'));
   ga.getXMLAnswer(detailsAi);

   function detailsAi(response){

	var answer = JSON.parse(response);

	g_form.setValue('u_dr_price', answer.price);
	g_form.setValue('u_dr_model', answer.model);
	g_form.setValue('u_dr_warnnty_ex', answer.warrenty);
	g_form.setValue('u_dr_description', answer.description);


   }

}
