sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    var sec = g_form.getSectionNames();

    //alert(sec);
    if (!newValue == '') {
        g_form.setSectionDisplay(sec[0], true);
        g_form.setSectionDisplay(sec[1], true);
        var gr = g_form.getReference('u_ai_device', getDetails);

        function getDetails(getD) {

            g_form.setValue('u_dr_price', getD.u_price);
            g_form.setValue('u_dr_model', getD.model_number);
            g_form.setValue('u_dr_warnnty_ex', getD.warranty_expiration);
            g_form.setValue('u_dr_description', getD.short_description);


        }
    } 


    //Type appropriate comment here, and begin script below

}
